    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_duraksaatleri);
        tanimlama();
        vericek();
        veridoldur();
        islemyap();
    }
    public void tanimlama()
    {
        spnr =(Spinner) findViewById(R.id.spinner);
        durak_gidis =(TextView) findViewById(R.id.duraklar);
        baslangic =(TextView) findViewById(R.id.gidis);
        pazartesi =(TextView) findViewById(R.id.pzrtesisaat);
        sali = (TextView) findViewById(R.id.salisaat);
        carsamba =(TextView) findViewById(R.id.crsmbasaat);
        persembe =(TextView) findViewById(R.id.persembesaat);
        cuma =(TextView) findViewById(R.id.cumasaat);
        cumartesi= (TextView) findViewById(R.id.cmrtesisaat);
        pazar=(TextView) findViewById(R.id.pazarsaat);
        donus=(TextView) findViewById(R.id.donus);
        durak_donus =(TextView) findViewById(R.id.donusduraklar);
        pazartesi_donus=(TextView) findViewById(R.id.pzrtesisaat_donus);
        sali_donus= (TextView) findViewById(R.id.salisaat_donus);
        carsamba_donus=(TextView) findViewById(R.id.crsmbasaat_donus);
        persembe_donus=(TextView) findViewById(R.id.persembesaat_donus);
        cuma_donus=(TextView) findViewById(R.id.cumasaat_donus);
        cumartesi_donus= (TextView) findViewById(R.id.cmrtesisaat_donus);
        pazar_donus =(TextView) findViewById(R.id.pazarsaat_donus);

    }
    public void vericek()
    {
        adp = new ArrayAdapter(this,R.layout.support_simple_spinner_dropdown_item,Hatlar);
    }
    public void veridoldur()
    {
        spnr.setAdapter(adp);
    }
    public void islemyap()
    {
        spnr.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {
            @Override
            public void onItemSelected(AdapterView<?> parent, View view, int position, long id) {
                durak_gidis.setText(Duraklar_Gidis[spnr.getSelectedItemPosition()]);
                baslangic.setText("GD");
                pazartesi.setText(Pazartesi[spnr.getSelectedItemPosition()]);
                sali.setText(Sali[spnr.getSelectedItemPosition()]);
                carsamba.setText(Carsamba[spnr.getSelectedItemPosition()]);
                persembe.setText(Persembe[spnr.getSelectedItemPosition()]);
                cuma.setText(Cuma[spnr.getSelectedItemPosition()]);
                cumartesi.setText(Cumartesi[spnr.getSelectedItemPosition()]);
                pazar.setText(Pazar[spnr.getSelectedItemPosition()]);
                donus.setText("DN");
                durak_donus.setText(Duraklar_Donus[spnr.getSelectedItemPosition()]);
                pazartesi_donus.setText(Pazartesi_Donus[spnr.getSelectedItemPosition()]);
                sali_donus.setText(Sali_Donus[spnr.getSelectedItemPosition()]);
                carsamba_donus.setText(Carsamba_Donus[spnr.getSelectedItemPosition()]);
                persembe_donus.setText(Persembe_Donus[spnr.getSelectedItemPosition()]);
                cuma_donus.setText(Cuma_Donus[spnr.getSelectedItemPosition()]);
                cumartesi_donus.setText(Cumartesi_Donus[spnr.getSelectedItemPosition()]);
                pazar_donus.setText(Pazar_Donus[spnr.getSelectedItemPosition()]);
            }

            @Override
            public void onNothingSelected(AdapterView<?> parent) {

            }
        });
    }
